import Web3Context from "../../src/Web3Context"
import { useContext, useEffect, useRef, useState } from "react"
import { Card, Image, Tooltip, Modal, Badge} from "antd";
const { Meta } = Card
import { FileSearchOutlined, ShoppingCartOutlined } from "@ant-design/icons"
import { supportedChains } from "../../src/networkProvider";
import {useMoralisWeb3Api, useMoralisWeb3ApiCall, useMoralisQuery, useWeb3ExecuteFunction} from "react-moralis"
import {getCollectionsByChain} from "../../src/collections"
import contractJson from "../../src/contracts/marketPlaceBoilerPlate.json"
import Moralis from "moralis"
// import Image from "next/image"

export default function NFTTokenIds(props) {
    const web3Context = useContext(Web3Context)

    // Moralis Hooks
    const {token} = useMoralisWeb3Api()
    const {fetch: getNFTTokenIds, data, error, isLoading} = useMoralisWeb3ApiCall(token.getAllTokenIds, {chain: web3Context.chainId, address: props.data && props.data.addr })
    const {fetch: getItemOnSale, data: nftOnSale} = useMoralisQuery("CreatedNftItem")
    const contractProcessor = useWeb3ExecuteFunction();
    
    // React Hooks
    const [collections, setCollections] = useState()
    const [buy, setBuy] = useState()
    const [isVisible, setIsVisible] = useState()
    const mktPlaceContract = useRef()

    useEffect(()=>{
        
        mktPlaceContract.current = web3Context.web3 && web3Context.chainId && new web3Context.web3.eth.Contract(contractJson.abi, supportedChains[web3Context.chainId].mktPlaceAddr)
        
        getNFTTokenIds()
        getItemOnSale()
        web3Context.chainId && setCollections(getCollectionsByChain(web3Context.chainId))
        // data && resloveImage()
    }, [web3Context.user, web3Context.chainId])

    async function buyNFT(nft){
        let metadata = JSON.parse(nft.metadata)
        let buy = {...nft, metadata: metadata}
        setBuy(buy)
        setIsVisible(true)
    }

    function sortCreatedNft() {
        return nftOnSale.map((nft)=>{
            return {
                token_address: nft.attributes.nftContract,
                token_id: nft.attributes.tokenId,
                sold: nft.attributes.sold,
                confirmed: nft.attributes.confirmed,
                seller: nft.attributes.seller,
                itemId: nft.attributes.itemId,
                price: nft.attributes.price,
                createdAt: nft.attributes.createdAt,
                objectId: nft.attributes.objectId,
                owner: nft.attributes.owner
            }
        })
    }

    function getNftInfo(nft) {
        let sortedItems = sortCreatedNft()
        return sortedItems?.filter((item)=>{
            return item.token_address == nft.token_address &&
                item.token_id == nft.token_id
        })
    }

    function isOnSale(nft) {
        let sortedItems = sortCreatedNft()
        let onSaleItem = sortedItems?.filter((item)=>{
            return item.token_address == nft?.token_address &&
                item.token_id == nft?.token_id && 
                !item.sold &&
                item.confirmed
        })
        return onSaleItem.length > 0
        // return true
    }

    async function handleBuyNft(nft) {
        let price = getNftInfo(nft)[0].price
        await contractProcessor.fetch({
            params: {
                contractAddress: supportedChains[web3Context.chainId].mktPlaceAddr,
                functionName: "createMarketSale",
                abi: contractJson.abi,
                params: {
                    nftContract: nft.token_address,
                    itemId: getNftInfo(nft)[0].itemId,
                },
                msgValue: price,
            }
        });
    }

    return <>
    
        <div style={{display: "flex", justifyContent: "center", flexWrap: "wrap"}}>
            {data && data.result.length > 0 ? 
                data.result.map((nft, id)=>{
                    return <Card
                                key={id}
                                hoverable
                                actions={[
                                    <Tooltip key={0} title="View On Blockexplorer">
                                        <FileSearchOutlined
                                            onClick={() => window.open(`${web3Context.chainId && supportedChains[web3Context.chainId].explorer}address/${nft.token_address}`, "_blank")}
                                        />
                                    </Tooltip>,
                                    <Tooltip key={1} title="Buy NFT">
                                        <ShoppingCartOutlined onClick={() => {buyNFT(nft)}} />
                                    </Tooltip>,
                                ]}
                                style={{ width: 240 , margin: "10px"}}
                                cover={<Image
                                    preview={false}
                                    src={JSON.parse(nft?.metadata)?.image || "error"}
                                    fallback="data:image/png;base64,"
                                    alt=""
                                    style={{ height: "300px" }}
                                />}
                            >
                            {isOnSale(nft) && <Badge.Ribbon text="Buy Now" color={"green"}/>}
                            <Meta title={nft.name} description={nft.token_id} />
                        </Card>
                }):
                collections && collections.map((collection, id)=>{
                    return <Card
                                key={id}
                                hoverable
                                actions={[
                                    <Tooltip key={0} title="View this collection">
                                        <a href={`/nft-tokenids/#${collection.addr}`}><FileSearchOutlined/></a>
                                    </Tooltip>,
                                ]}
                                style={{ width: 240 , margin: "10px"}}
                                cover={<Image
                                    preview={false}
                                    src={collection.image || "error"}
                                    fallback="data:image/png;base64,"
                                    alt=""
                                    style={{ height: "300px" }}
                                />}
                            >
                            {isOnSale(collection) && <Badge.Ribbon text="Buy Now" color={"green"}/>}
                            <Meta title={collection.name} description={collection.addr} />
                        </Card>
                })
            }
            {isOnSale(buy) &&
                <Modal
                    title={`Buy ${buy?.name} #${buy?.token_id}`}
                    visible={isVisible}
                    onCancel={() => setIsVisible(false)}
                    onOk={() => {handleBuyNft(buy)}}
                    okText="Buy"
                >   
                    <Badge.Ribbon text={Math.floor(getNftInfo(buy)[0].price/1e18 * 1e5)/1e5} color="green"/>
                    <Image preview={false} src={buy?.metadata?.image || "error"} style={{ height: "300px" }}/>
                </Modal>
            }
        </div>
    </>
}